import React from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Gallery from './Art&Craft/Art';
import Art_data from './Art&Craft/Art_data';
import Port from './Portraits/Portraits';
import Port_data from './Portraits/Port_data';
import Archi from './Architec/Archi';
import Archi_data from './Architec/Archi_data';
import Graphic from './Graphic/Graphic';
import Graphic_data from './Graphic/Graphic_data';
import { NavLink } from 'react-router-dom';


interface TabPanelProps {
  children?: React.ReactNode;
  index: any;
  value: any;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function a11yProps(index: any) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    flexGrow: 1,
    width: '100%',
  },
}));

export default function ScrollableTabsButtonAuto() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
        >
          <Tab label="Art & Craft" {...a11yProps(0)} />
          <Tab label="Architectural Design" {...a11yProps(1)} />
          <Tab label="Graphic Design" {...a11yProps(2)} />
          <Tab label="Portraits" {...a11yProps(3)} />
        </Tabs>
      </AppBar>
      <NavLink to="/" className="btn btn-primary my-3 mx-3">Back</NavLink>
      <TabPanel value={value} index={0}>
        
    
         
              
                <div className="row">
                {
                  Art_data.map((val, ind) => {
                                    return <Gallery
                                        img={val.img}
                                        key={ind}
                                    />
                                    })  

                }
                </div>


      </TabPanel>
      <TabPanel value={value} index={1}>
      <div className="container-fluid my-5">
         
              
         <div className="row ">
         {
           Archi_data.map((val, ind) => {
                             return <Archi
                                 img={val.img}
                                 key={ind}
                             />
                             })  

         }
         </div>
</div>
      </TabPanel>
      <TabPanel value={value} index={2}>
      <div className="container-fluid my-5">
         
              
         <div className="row">
         {
           Graphic_data.map((val, ind) => {
                             return <Graphic
                                 img={val.img}
                                 key={ind}
                             />
                             })  

         }
         </div>
</div>

      </TabPanel>
      <TabPanel value={value} index={3}>
      <div className="container-fluid my-5">
         
              
         <div className="row">
         {
           Port_data.map((val, ind) => {
                             return <Port
                                 img={val.img}
                                 key={ind}
                             />
                             })  

         }
         </div>
</div>
      </TabPanel>
     
    </div>
  );
}
