import React,{useState} from 'react';

const Art = (props) => {
    const [col,setCol]=useState("")
    const bigimg=()=>{
        setCol("col-12")
    }
    return (
        <>

        <div className="col-lg-3 col-md-4 col-6 mx-auto" className={col}>
            <figure>
            <img className="img-fluid" src={props.img} alt="image" onClick={bigimg()} >
        </img>
            </figure>
        </div>
       
        




        </>
    )
}

export default Art
